mapper.select:
select * from review
where delyn='N'
  and name = #{name}
  and addr = #{addr}
order by vidx desc
limit #{reviewStart},#{reviewPerPageNum}

select count(*)
  from review
where delyn='N'
  and name = #{name}
  and addr = #{addr}

mapper.insert:
insert into review(
		title
	,	content
	,	writer
	,	hit
	,	likeCnt
	,	filename
	,	wdate
	,	delyn
	,	midx
	,	name
	,	addr
)values(
		#{title}
	,	#{content}
	,	#{writer}
	,	0
	,	0
	, 	#{filename}
	,	now()
	,	'N'
	,	#{midx}
	,	#{name}
	,	#{addr}
)

mapper.update:
update review
   set likeCnt = likeCnt+1
 where vidx = #{vidx}

update reviewlike
   set likeCheck = 1
 where vidx = #{vidx}
   and id = #{id}

mapper.select:
select count(*)
  from reviewlike
 where vidx = #{vidx}
   and id = #{id}
