mapper.update:
UPDATE event	
	 SET rec = rec + 1
	 WHERE eidx = #{eidx}

mapper.insert:
INSERT INTO event (
	eidx
	,title
	,contents
	,rec
	,wdate
	,writer
) VALUES (
	eidx_seq.nextval
	,#{title}
	,#{contents}
	,0
	,sysdate
	,#{writer}
)

mapper.update:
 UPDATE event
 	SET title = #{title}
 		,contents = #{contents}
 	WHERE eidx=#{eidx}
