mapper.update:
UPDATE member
SET pwd=#{pwd} 
WHERE email= #{email}

mapper.select:
SELECT COUNT(id) 
	FROM member 
	WHERE id= #{id}

SELECT COUNT(name) 
	FROM member 
	WHERE name= #{name}

SELECT COUNT(email) 
	FROM member 
	WHERE email= #{email}

SELECT COUNT(nickname) 
	FROM member 
	WHERE nickname= #{nickname}

mapper.insert:
INSERT INTO member (
	midx
	,id
	,pwd
	,name
	,birth
	,nickname
	,gender	
	,email
	,phone
	,wdate
	,delyn
	) VALUES (
		midx_seq.nextval
		,#{id}
		,#{pwd}
		,#{name}
		,#{birth}
		,#{nickname}
		,#{gender}
		,#{email}
		,#{phone}
		,sysdate	
		,'N'
	)

mapper.select:
SELECT id
	FROM member
	WHERE name = #{name}
	AND email = #{email}
